package javaexample;

public class EvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=50;
		for(int i=2;i<=num;i++)
		{
			if(i%2==0)
			{
				System.out.println("The number"+i+"is even");
				
			}
			
	else
			{
				System.out.println("The number is not even");
	}
		}

	}

}
